package com.java;

public class OperacionesBasicas {

	public static void main(String[] args) {
		double a = 5.6;
		double b = 7.5;
		double c = 14.55;

		double d = a + b + c;
		double e = a * b * c;
		double f = a / b / c;

		System.out.println("La suma del valor " + a + " mas el valor " + b + " mas el valor " + c + " es igual a: " + d);
		System.out.println("El resultado de la multibplicacion de estos tres valores es igual a: " +e);
		System.out.println("El resultado de la division de estos tres valores es igual a: " +f);
 
		// TODO Auto-generated method stub

	}

}
